jet = jet_input

seen = {}
top = 0
jet_index = 0
placed = 0
added = 0
map = set([(x, 0) for x in range(7)])
while placed < 1000000000000:
    curr_piece = set([(shape[0], shape[1] + top + 4) for shape in shapes[placed % 5]])
    while True:
        # Horizontal
        if jet[jet_index % len(jet)] == '<':
            if all([x != 0 for (x, y) in curr_piece]):
                curr_piece = set([(x - 1, y) for (x, y) in curr_piece])
            if any([pose in map for pose in curr_piece]):
                curr_piece = set([(x + 1, y) for (x, y) in curr_piece])
        else:
            if all([x != 6 for (x, y) in curr_piece]):
                curr_piece = set([(x + 1, y) for (x, y) in curr_piece])
            if any([pose in map for pose in curr_piece]):
                curr_piece = set([(x - 1, y) for (x, y) in curr_piece])
        jet_index += 1

        # Vertical
        curr_piece = set([(x, y - 1) for (x, y) in curr_piece])
        if any([pose in map for pose in curr_piece]):
            curr_piece = set([(x, y + 1) for (x, y) in curr_piece])
            for pose in curr_piece:
                map.add(pose)
            top = max([y for (x, y) in map])

            actual = (jet_index % len(jet), placed % 5)
            if actual in seen and placed >= 2022:
                (oldPlaced, oldy) = seen[actual]
                diff_y, diff_placed = top - oldy, placed - oldPlaced
                multi = (1000000000000 - placed) // diff_placed
                added += multi * diff_y
                placed += multi * diff_placed
            seen[actual] = (placed, top)
            break
    placed += 1
    if placed == 2022:
        print(top)

print("----- PART 2 -----")
print(top + added)
